import React from "react";
//import Card from "../Components/Card";
import { useContextGlobal } from "../Components/utils/global.context";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {

  const {state}=useContextGlobal()

  const listaFavoritos=JSON.parse(localStorage.getItem('favorit'))
  console.log({ listaFavoritos})

  const handleDelete = (favoId) => {
    // Filtrar la lista para eliminar el elemento por su ID
    const updatedFavoritos = listaFavoritos.filter(favo => favo.id !== favoId);
    
    // Actualizar el localStorage con la lista modificada
    localStorage.setItem('favorit', JSON.stringify(updatedFavoritos));
    
    // Actualizar el componente (por ejemplo, recargar la p치gina)
    // window.location.reload();
    // O bien, actualizar el estado del componente si est치s utilizando un estado local
    // ...
  };
  
  return (
    <>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        {/* este componente debe consumir los destacados del localStorage */}
         {/* Deberan renderizar una Card por cada uno de ellos */}
        {listaFavoritos.length>0 && listaFavoritos.map((favo)=>
         
          state.dentistas.map((dent)=>
            favo.id===dent.id&&
            //<Card odontologo={favo} key={favo.id}/>
            <div key={favo.id}>
               <h2>Name: <span>{favo.name}</span></h2>
               <h2>UserName: <span>{favo.username}</span></h2>
               <h2>Id: <span>{favo.id}</span></h2>
               <button onClick={handleDelete(favo.id)}>游뛅</button>
            </div>
           
          )
        )}
       
       
      </div>
    </>
  );
};

export default Favs;
