import React from "react";
//import Card from "../Components/Card";
import { useContextGlobal } from "../Components/utils/global.context";
import imageDentist from "..//images/doctor.jpg"
import { Link } from "react-router-dom";
import { useState } from "react";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {

  const {state}=useContextGlobal()
  const [favoritos, setFavoritos] = useState(
    JSON.parse(localStorage.getItem("favorit")) || []
  );

  // const listaFavoritos=JSON.parse(localStorage.getItem('favorit'))
  // console.log({ listaFavoritos})

  const handleDeleteFavorito = (id) => {
    const updatedFavoritos = favoritos.filter((favo) => favo.id !== id);
    setFavoritos(updatedFavoritos);
    localStorage.setItem("favorit", JSON.stringify(updatedFavoritos));
  };
  
  return (
    <div className="paginaFavoritos">
      <h1 className="tituloFavs">Dentists Favs</h1>
      <div className="card-grid">
        {/* este componente debe consumir los destacados del localStorage */}
         {/* Deberan renderizar una Card por cada uno de ellos */}
        {favoritos.length>0 && favoritos.map((favo)=>
         
          state.dentistas.map((dent)=>
            favo.id===dent.id&&
            //<Card odontologo={favo} key={favo.id}/>
            <div className='card'>
                <Link to={'/detail/'+ favo.id}   key={favo.id}>
                   <img className="imageDentist" src={imageDentist}alt="" />
                   <h2 className="dataDentist">{favo.name}</h2>
                   <h2 className="dataDentist">{favo.username}</h2>
                   <h2 className="dataDentist">Id: <span>{favo.id}</span></h2>  
                 </Link>
                 <button onClick={() => handleDeleteFavorito(favo.id)} className="deleteFavorito">ðŸš®</button>
            </div>
           
           
          )
        )}
       
       
      </div>
    </div>
  );
};

export default Favs;
