import React, { useEffect,useState } from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";
import "../../src/index.css"
import imageDentist from "../images/doctor.jpg"

const Card = ({odontologo}) => {

const {state,dispatch}=useContextGlobal() 
const [buttonColor, setButtonColor] = useState('')
const [isFavorito, setIsFavorito] = useState(false);


  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    localStorage.setItem('favorit', JSON.stringify(odontologo))
    const odontologosLocalStore= localStorage.getItem('favorit')
    dispatch({type:'add_favorites',payload:odontologosLocalStore})
    state.favorites.map((f)=>f.id===odontologo.id &&  setIsFavorito(true))
  if(!isFavorito){
    setButtonColor('green');
  }
  else{
    alert('El odontologo ya esta en favoritos')
  }
}

  
 
  return (
    <div className='card'>
        {/* En cada card deberan mostrar en name - username y el id */}
        <Link to={'/Detail/' + odontologo.id}>
          <img className="imageDentist" src={imageDentist}alt="" />
          <h2 className="dataDentist">{odontologo.name}</h2>
          <h3 className="dataDentist">{odontologo.username}</h3>
          <h3 className="dataDentist">Id: <span>{odontologo.id}</span></h3>
        </Link>
       

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button style={{ backgroundColor: buttonColor }}onClick={addFav} className="favButton">⭐</button>
    </div>
  );
};

export default Card;





