import { useEffect } from 'react'
import { useContextGlobal } from '../Components/utils/global.context'
import { useParams } from 'react-router-dom'
import axios from 'axios'


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {
 
  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  const params=useParams()
 
  const {state, dispatch}= useContextGlobal()
  
 
  const endpoint2=`https://jsonplaceholder.typicode.com/users/${params.id}`

  useEffect(()=>{
    axios(endpoint2)
    .then(res=> dispatch({type:'get_odontologo', payload: res.data},console.log(res.data)))
  },[])
  



  return (
    <>
      <h1>Detail Dentist {state.odontologo.id} </h1>
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
      <div>
        <h2>Name: <span>{state.odontologo.name}</span></h2>
        <h2>Email: <span>{state.odontologo.email}</span></h2>
        <h2>Phone: <span>{state.odontologo.phone}</span></h2>
        <h2>WebSite: <span>{state.odontologo.website}</span></h2>
      </div>
      
    </>
  )
}

export default Detail
