import React, {useEffect,useState } from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";
import "../../src/index.css"
import imageDentist from "../images/doctor.jpg"

const Card = ({odontologo}) => {

const {state,dispatch}=useContextGlobal() 
const [buttonColor, setButtonColor] = useState('')
const [isFavorito, setIsFavorito] = useState(false);

useEffect(() => {
  const estaEnFavoritos = state.favorites.some(favo => favo.id === odontologo.id);
  setIsFavorito(estaEnFavoritos);
}, [state.favorites, odontologo.id]);

//  useEffect(() => {
//    state.favorites.map((favo)=>favo.id===odontologo.id && setIsFavorito(true))
//  }, [state.favorites, odontologo.id]);

console.log(isFavorito)


  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
  if(!isFavorito){
    dispatch({type:'add_favorites',payload:odontologo})
    setButtonColor('green');
    setIsFavorito(true)
  }
  else{
    alert('El odontologo ya esta en favoritos')
  }
}

useEffect(() => {
  localStorage.setItem("favorit", JSON.stringify(state.favorites));
}, [state.favorites]);

  // const deleteFavs=(id)=>{
  //    const listaFavoritos=JSON.parse(localStorage.getItem('favorit'))
  //    const nuevosFovoritos=listaFavoritos.filter((favorito)=>favorito.id !== id )
