import React from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";



const Card = ({odontologo}) => {


 



  
const {state,dispatch}=useContextGlobal() 



  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage

    const dis=dispatch({type:'add_favorites', payload:state.favorites})
    return(localStorage.setItem('dentist',JSON.stringify(state.favorites))


    )
    
  }
  console.log(state.favorites)
  return (
    <div className="card">
        {/* En cada card deberan mostrar en name - username y el id */}
        <Link to={'/Detail/' + odontologo.id}>
          <h2>{odontologo.name}</h2>
          <h3>{odontologo.username}</h3>
          <h3>{odontologo.id}</h3>
        </Link>
       

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
