import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";
import "../../src/index.css";
import imageDentist from "../images/doctor.jpg";

const Card = ({ odontologo }) => {
  const { state, dispatch } = useContextGlobal();
  const [buttonColor, setButtonColor] = useState('');
  const [isFavorito, setIsFavorito] = useState(false);

  useEffect(() => {
    const estaEnFavoritos = state.favorites.some(favo => favo.id === odontologo.id);
    setIsFavorito(estaEnFavoritos);
  }, [state.favorites, odontologo.id]);

  const addFav = () => {
    if (!isFavorito) {
      dispatch({ type: 'add_favorites', payload: odontologo });
      setButtonColor('green');
      setIsFavorito(true);
    }
  };

  useEffect(() => {
    localStorage.setItem("favorit", JSON.stringify(state.favorites));
  }, [state.favorites]);

  const deleteFavs = (id) => {
    const nuevoEstado = state.favorites.filter(ne => ne.id !== id);
    dispatch({ type: 'add_favorites', payload: nuevoEstado });
    localStorage.setItem("favorit", JSON.stringify(nuevoEstado));
    setIsFavorito(false);
  };

  return (
    <div className='card'>
      {/* ... Resto del c√≥digo */}
      {isFavorito ?
        <button onClick={() => deleteFavs(odontologo.id)} style={{ background: buttonColor }} className="ButtonDeleteFav">üöÆ</button> :
        <button onClick={addFav} style={{ backgroundColor: buttonColor }} className="favButton">‚≠ê</button>}
    </div>
  );
};

export default Card;
