 import React, {useEffect,useState } from "react";
 import { Link } from "react-router-dom";
 import { useContextGlobal } from "./utils/global.context";
 import "../../src/index.css"
 import imageDentist from "../images/doctor.jpg"

 const Card = (dent) => {

 const {state,dispatch}=useContextGlobal() 
 const [buttonColor, setButtonColor] = useState('')
 const [isFavorito, setIsFavorito] = useState(false);

 useEffect(() => {
   const estaEnFavoritos = state.favorites.some(favo => favo.id === dent.id);
   setIsFavorito(estaEnFavoritos);
 }, [state.favorites, dent.id]);

console.log(isFavorito)

   const addFav = ()=>{

   // Aqui iria la logica para agregar la Card en el localStorage

  
     dispatch({type:'add_favorites',payload:dent})
     setButtonColor('green');
     setIsFavorito(true)
   
 }

 const removeFav=()=>{
  
 }

 useEffect(() => {
   localStorage.setItem("favorit", JSON.stringify(state.favorites));
 }, [state.favorites]);

 

 return (
  <div className='card'>
    {state.dentistas.map((dent) => (
      <Link to={'/Detail/' + dent.id} key={dent.id}>
        <div>
          <img className="imageDentist" src={imageDentist} alt="" />
          <h2 className="dataDentist">{dent.name}</h2>
          <h3 className="dataDentist">{dent.username}</h3>
          <h3 className="dataDentist">Id: <span>{dent.id}</span></h3>
        </div>

        {/* Bot√≥n de favorito o eliminar, deber√≠a estar relacionado con el estado de cada tarjeta */}
        {/* Por ejemplo (solo como ejemplo, puede requerir adaptaci√≥n): */}
        {dent.isFavorito ?  
          <button style={{ background: buttonColor }} className="ButtonDeleteFav">üöÆ</button> : 
          <button style={{ backgroundColor: buttonColor }} onClick={() => addFav(dent.id)} className="favButton">‚≠ê</button>}
      </Link>
    ))}
  </div>
);
