import { useState } from "react";

const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  
  const[estados,setEstados]=useState({
    fail:"",
    validacion:false,
    error:false,
    name:"",
    email:""
  })


  // const [fail, setFail]=useState("")
  // const [validacion,setValidacion]=useState(false)
